import { sendemail } from "./apicalls/email";
import { useState } from "react";
import { jsPDF } from "jspdf";

const data = {
  Sheet1: [
    {
      SNo: 1,
      Name: "madhi",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "swigy",
    },
    {
      SNo: 2,
      Name: "manoj",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "zomato",
    },
    {
      SNo: 3,
      Name: "arun",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "blink it technology",
    },
  ],
};

// Function to add delay
const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

const App = () => {
  const [failedEmails, setFailedEmails] = useState([]);
  const [progress, setProgress] = useState(0);
  const [isSending, setIsSending] = useState(false);
  const [totalEmails, setTotalEmails] = useState(0);

  const generatePDF = async (record) => {
    const doc = new jsPDF();
    doc.setFont("times", "normal");
    doc.setFontSize(12);

    const currentDate = new Date().toLocaleDateString("en-US", {
        year: "numeric",
        month: "long",
        day: "numeric",
    });

    const coverLetterText = `
  Naveen M
  naveenacp24@gmail.com | +91 9632557859
  LinkedIn: https://www.linkedin.com/in/naveen4455 | Portfolio: https://naveenm.in

  ${currentDate}

  ${record.Name}
  ${record.Company}

  Subject: Application for a Relevant Role at ${record.Company}

  Dear ${record.Name},

  I am reaching out to express my keen interest in any relevant opportunities at ${record.Company}. I am a final-year 
  Master of Computer Applications (MCA) student at SRM Institute of Science and Technology, with expertise in 
  Full-Stack Web Development using the MERN stack (MongoDB, Express.js, React.js, and Node.js).

  I have gained valuable experience working as a Web Developer Intern, where I enhanced my technical skills and 
  delivered successful projects. Additionally, I have independently completed freelance projects, including:

  - Tuition Management System – Implemented features like student and admin management, attendance, and 
          marks tracking, integrated with Twilio API for absent notifications.
  - Company Portfolio Website – Built a professional website for client engagement with integrated contact features 
          using Nodemailer.


  I am proficient in JavaScript, Python, REST API integration, and authentication mechanisms, enabling me to 
  develop secure and efficient applications. I am highly motivated to apply my skills to contribute to 
  ${record.Company}’s projects and goals.

  I have attached my resume for your review. I would love the opportunity to discuss how my skills align with 
  the needs of ${record.Company}. Please feel free to reach out at your convenience.
  Looking forward to your response.

  Best regards,  
  Naveen M`;

    const lineHeight = 7;
    let yPosition = 10;

    coverLetterText.split("\n").forEach((line) => {
        doc.text(line, 10, yPosition);
        yPosition += lineHeight;
    });

    const pdfBlob = doc.output("blob");

    // Convert PDF to Base64
    return new Promise((resolve) => {
        const reader = new FileReader();
        reader.readAsDataURL(pdfBlob);
        reader.onloadend = () => {
            resolve({
                filename: `Naveen_M_${record.Company.replace(/\s+/g, "_")}_Cover_Letter.pdf`,
                content: reader.result.split(",")[1], // Extract Base64 content
                encoding: "base64",
            });
        };
    });
};


  const handleSendEmails = async () => {
    setIsSending(true);
    setProgress(0);
    setFailedEmails([]);
    setTotalEmails(data.Sheet1.length);

    let failedList = [];

    for (const [index, record] of data.Sheet1.entries()) {
      const coverLetterFile = await generatePDF(record);
      console.log(coverLetterFile)
      const payload = {
        name: record.Name,
        email: record.Email,
        subject: `Application for a Relevant Opportunity at ${record.Company}`,
        message: `
          Dear ${record.Name},

          I hope this message finds you well.

          I am reaching out to express my keen interest in any relevant opportunities at ${record.Company}. I am a final-year Master of 
          Computer Applications (MCA) student at SRM Institute of Science and Technology, with expertise in Full-Stack Web 
          Development using the MERN stack (MongoDB, Express.js, React.js, and Node.js).

          I have gained valuable experience working as a Web Developer Intern, where I enhanced my technical skills and delivered 
          successful projects. Additionally, I have independently completed freelance projects, including:

          ● Tuition Management System – Implemented features like student and admin management, attendance, and marks tracking, 
            integrated with Twilio API for absent notifications.  
          ● Company Portfolio Website – Built a professional website for client engagement with integrated contact features using 
            Nodemailer.

          My strong foundation in JavaScript, Python, API integration, and secure authentication positions me to handle complex 
          development challenges.

          Please find my resume and cover letter attached for your reference. I would be grateful for an opportunity to discuss how my 
          skills align with the needs of ${record.Company}.

          Thank you for considering my application. I look forward to the possibility of contributing to your team.

          Best regards,  
          Naveen M  
          LinkedIn: https://www.linkedin.com/in/naveen4455 | Portfolio: https://naveenm.in  
          +91 9632557859
        `,
        attachments: [coverLetterFile],
      };

      try {
        console.log(`Sending email to ${record.Email}...`);
        const response = await sendemail(payload);

        if (response.status === "success") {
          console.log(`✅ Success: Email sent to ${record.Email}`);
        } else {
          console.error(`❌ Failed to send email to ${record.Email} - ${response.message}`);
          failedList.push(record);
        }
      } catch (error) {
        console.error(`❌ Error sending email to ${record.Email} - ${error.message}`);
        failedList.push(record);
      }

      setProgress(index + 1);

      if (index < data.Sheet1.length - 1) {
        console.log(`⏳ Waiting 3 seconds before sending the next email...`);
        await delay(3000);
      }
    }

    setFailedEmails(failedList);
    setIsSending(false);

    if (failedList.length === 0) {
      alert("✅ All emails sent successfully!");
    } else {
      alert(`❌ Failed to send ${failedList.length} email(s).`);
    }
  };

  return (
    <div>
      <h1>Send Bulk Emails</h1>
      <button onClick={handleSendEmails} disabled={isSending}>
        {isSending ? "Sending..." : "Send Emails"}
      </button>

      {totalEmails > 0 && (
        <div style={{ marginTop: "10px" }}>
          <progress value={progress} max={totalEmails} style={{ width: "100%" }} />
          <p>{progress}/{totalEmails} emails sent</p>
        </div>
      )}

      {failedEmails.length > 0 && (
        <div>
          <h2>Failed Emails:</h2>
          <ul>
            {failedEmails.map((record) => (
              <li key={record.Email}>{record.Name} ({record.Email}) - {record.Company}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
