import { sendemail } from "./apicalls/email";
import { useState } from "react";

const data = {
  Sheet1: [
    {
      SNo: 1,
      Name: "khdbqekhfbe",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "kSHBFAKF",
    },
    {
      SNo: 2,
      Name: "ksjbfaekjbfef",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "KQWJBFQEF",
    },
    {
      SNo: 4,
      Name: "ADKJBFDKJGBSG",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "qLJFBAEKJ,GNFE",
    },
  ],
};

// ‚úÖ Function to add delay
const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

const App = () => {
  const [failedEmails, setFailedEmails] = useState([]);
  const [progress, setProgress] = useState(0);
  const [isSending, setIsSending] = useState(false);
  const [totalEmails, setTotalEmails] = useState(0);

  const handleSendEmails = async () => {
    setIsSending(true);
    setProgress(0);
    setFailedEmails([]);
    setTotalEmails(data.Sheet1.length);

    let failedList = [];

    for (const [index, record] of data.Sheet1.entries()) {
      const payload = {
        name: record.Name,
        email: record.Email,
        subject: `Application for a Relevant Opportunity at ${record.Company}`,
        message: `
Dear ${record.Name},  

I hope this message finds you well.    

I am reaching out to express my keen interest in any relevant opportunities at **${record.Company}**. I am a final-year Master of Computer Applications (MCA) student at **SRM Institute of Science and Technology**, with expertise in Full-Stack Web Development using the MERN stack (MongoDB, Express.js, React.js, and Node.js).  

I have gained valuable experience working as a Web Developer Intern, where I enhanced my technical skills and delivered successful projects. Additionally, I have independently completed freelance projects, including:  

‚úÖ **Tuition Management System** ‚Äì Implemented features like student and admin management, attendance, and marks tracking, integrated with Twilio API for absent notifications.  
‚úÖ **Company Portfolio Website** ‚Äì Built a professional website for client engagement with integrated contact features using Nodemailer.  

My strong foundation in **JavaScript**, **Python**, **API integration**, and **secure authentication** positions me to handle complex development challenges.  

Please find my resume and cover letter attached for your reference. I would be grateful for an opportunity to discuss how my skills align with the needs of **${record.Company}**.  

Thank you for considering my application. I look forward to the possibility of contributing to your team.  

Best regards,  
**Naveen M**  
üëâ [LinkedIn](https://www.linkedin.com/in/naveen4455) | üåê [Portfolio](https://naveenm.in) | üìû +91 9632557859  
        `,
      };

      try {
        console.log(`Sending email to ${record.Email}...`);
        const response = await sendemail(payload);

        if (response.status === "success") {
          console.log(`‚úÖ Success: Email sent to ${record.Email}`);
        } else {
          console.error(`‚ùå Failed to send email to ${record.Email} - ${response.message}`);
          failedList.push(record);
        }
      } catch (error) {
        console.error(`‚ùå Error sending email to ${record.Email} - ${error.message}`);
        failedList.push(record);
      }

      // ‚úÖ Update progress
      setProgress(index + 1);

      // ‚úÖ Add 3-second delay before next email
      if (index < data.Sheet1.length - 1) {
        console.log(`‚è≥ Waiting 3 seconds before sending the next email...`);
        await delay(3000); // 3000ms = 3 seconds
      }
    }

    setFailedEmails(failedList);
    setIsSending(false);

    // ‚úÖ Show summary alert
    if (failedList.length === 0) {
      alert("‚úÖ All emails sent successfully!");
    } else {
      alert(`‚ùå Failed to send ${failedList.length} email(s).`);
    }
  };

  return (
    <div>
      <h1>Send Bulk Emails</h1>
      <button onClick={handleSendEmails} disabled={isSending}>
        {isSending ? "Sending..." : "Send Emails"}
      </button>

      {/* ‚úÖ Progress Bar */}
      {totalEmails > 0 && (
        <div style={{ marginTop: "10px" }}>
          <progress
            value={progress}
            max={totalEmails}
            style={{ width: "100%" }}
          />
          <p>
            {progress}/{totalEmails} emails sent
          </p>
        </div>
      )}

      {/* ‚úÖ Failed Email List */}
      {failedEmails.length > 0 && (
        <div>
          <h2>Failed Emails:</h2>
          <ul>
            {failedEmails.map((record) => (
              <li key={record.Email}>
                {record.Name} ({record.Email}) - {record.Company}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
