import { sendemail } from "./apicalls/email";
import { useState } from "react";

const data = {
  Sheet1: [
    {
      SNo: 1,
      Name: "Akanksha Puri",
      Email: "navenacp24@gmail.com",
      Title: "Associate Director HR",
      Company: "SourceFuse Technologies",
    },

  ],
};

const App = () => {
  const [failedEmails, setFailedEmails] = useState([]);
  const [progress, setProgress] = useState(0);
  const [isSending, setIsSending] = useState(false);
  const [totalEmails, setTotalEmails] = useState(0);

  const handleSendEmails = async () => {
    setIsSending(true);
    setProgress(0);
    setFailedEmails([]);
    setTotalEmails(data.Sheet1.length);

    let failedList = [];

    for (const [index, record] of data.Sheet1.entries()) {
      const payload = {
        name: record.Name,
        email: record.Email,
        subject: `Application for a Suitable Position at ${record.Company}`,
        message: `
Dear ${record.Name},

I hope this email finds you well. I am writing to express my interest in any suitable openings at ${record.Company}. With my strong background in MERN Stack Development, React.js, and API integration, I am confident that I can contribute effectively to your team.

Please find my resume and cover letter attached for your review. I would be grateful for the opportunity to discuss how my skills align with the needs of ${record.Company}.

Thank you for considering my application. I look forward to hearing from you.

Best regards,  
Naveen M  
[LinkedIn Profile] | [Portfolio Website] | [Contact Number]
        `,
      };

      try {
        console.log(`Sending email to ${record.Email}...`);
        await sendemail(payload);
        console.log(`✅ Success: Email sent to ${record.Email}`);
      } catch (error) {
        console.error(`❌ Failed to send email to ${record.Email}`, error);
        failedList.push(record);
      }

      // ✅ Update progress
      setProgress(index + 1);
    }

    setFailedEmails(failedList);
    setIsSending(false);

    // ✅ Show summary alert
    if (failedList.length === 0) {
      alert("✅ All emails sent successfully!");
    } else {
      alert(`❌ Failed to send ${failedList.length} email(s).`);
    }
  };

  // ✅ Retry Sending Failed Emails
  const handleRetry = async () => {
    setIsSending(true);
    setProgress(0);
    setTotalEmails(failedEmails.length);

    let retryFailedList = [];

    for (const [index, record] of failedEmails.entries()) {
      const payload = {
        name: record.Name,
        email: record.Email,
        subject: `Application for a Suitable Position at ${record.Company}`,
        message: `
Dear ${record.Name},

I hope this email finds you well. I am writing to express my interest in any suitable openings at ${record.Company}. With my strong background in MERN Stack Development, React.js, and API integration, I am confident that I can contribute effectively to your team.

Please find my resume and cover letter attached for your review. I would be grateful for the opportunity to discuss how my skills align with the needs of ${record.Company}.

Thank you for considering my application. I look forward to hearing from you.

Best regards,  
Naveen M  
[LinkedIn Profile] | [Portfolio Website] | [Contact Number]
        `,
      };

      try {
        console.log(`Retrying email to ${record.Email}...`);
        await sendemail(payload);
        console.log(`✅ Success: Retried email sent to ${record.Email}`);
      } catch (error) {
        console.error(`❌ Failed again for ${record.Email}`, error);
        retryFailedList.push(record);
      }

      // ✅ Update progress
      setProgress(index + 1);
    }

    setFailedEmails(retryFailedList);
    setIsSending(false);

    if (retryFailedList.length === 0) {
      alert("✅ All retry emails sent successfully!");
    } else {
      alert(`❌ Failed to send ${retryFailedList.length} email(s) after retry.`);
    }
  };

  return (
    <div>
      <h1>Send Bulk Emails</h1>
      <button onClick={handleSendEmails} disabled={isSending}>
        {isSending ? "Sending..." : "Send Emails"}
      </button>

      {/* ✅ Progress Bar */}
      {totalEmails > 0 && (
        <div style={{ marginTop: "10px" }}>
          <progress
            value={progress}
            max={totalEmails}
            style={{ width: "100%" }}
          />
          <p>
            {progress}/{totalEmails} emails sent
          </p>
        </div>
      )}

      {/* ✅ Failed Email List */}
      {failedEmails.length > 0 && (
        <div>
          <h2>Failed Emails:</h2>
          <ul>
            {failedEmails.map((record) => (
              <li key={record.Email}>
                {record.Name} ({record.Email}) - {record.Company}
              </li>
            ))}
          </ul>
          <button onClick={handleRetry} disabled={isSending}>
            {isSending ? "Retrying..." : "Retry Failed Emails"}
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
