import { sendemail } from "./apicalls/email";
import { useState } from "react";
import { jsPDF } from "jspdf";

// Sample Data
const data = {
  Sheet1: [
    {
      SNo: 1,
      Name: "kiran",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "kwhcbfqekfe",
    },
    {
      SNo: 2,
      Name: "arjun",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "Technologies",
    },
    {
      SNo: 3,
      Name: "puni",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "puni Technologies",
    },
  ],
};

// Delay function to prevent rate limits
const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

const App = () => {
  const [failedEmails, setFailedEmails] = useState([]);
  const [progress, setProgress] = useState(0);
  const [isSending, setIsSending] = useState(false);
  const [totalEmails, setTotalEmails] = useState(0);

  // PDF Generation Function
  const generatePDF = async (record) => {
    const doc = new jsPDF();
    doc.setFont("times", "normal");
    doc.setFontSize(12);

    const currentDate = new Date().toLocaleDateString("en-US", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });

    const coverLetterText = `
  Naveen M
  naveenacp24@gmail.com | +91 9632557859
  LinkedIn: https://www.linkedin.com/in/naveen4455 | Portfolio: https://naveenm.in

  ${currentDate}

  ${record.Name}
  ${record.Company}
  ${record.Email}

  Subject: Application for a Relevant Role at ${record.Company}

  Dear ${record.Name},

  I am reaching out to express my keen interest in any relevant opportunities at ${record.Company}. 
  I am a Full-Stack Web Developer skilled in MERN stack (MongoDB, Express.js, React.js, Node.js).

  I have worked as a Web Developer Intern and completed freelance projects, including:
  ‚óè Tuition Management System ‚Äì Student and admin management, attendance tracking, Twilio API notifications.
  ‚óè Company Portfolio Website ‚Äì Professional site with integrated contact features using Nodemailer.

  I have attached my resume for your review. Looking forward to discussing how my skills align with your needs.

  Best regards,
  Naveen M`;

    let yPosition = 10;
    const lineHeight = 7;

    coverLetterText.split("\n").forEach((line) => {
      doc.text(line, 10, yPosition);
      yPosition += lineHeight;
    });

    const pdfBlob = doc.output("blob");
    const sanitizedFileName = `Naveen_M_${record.Company.replace(/\s+/g, "_")}_Cover_Letter.pdf`;
    return new File([pdfBlob], sanitizedFileName, { type: "application/pdf" });
  };

  // Function to Send Emails
  const handleSendEmails = async () => {
    setIsSending(true);
    setProgress(0);
    setFailedEmails([]);
    setTotalEmails(data.Sheet1.length);

    let failedList = [];

    for (const [index, record] of data.Sheet1.entries()) {
      const coverLetterFile = await generatePDF(record);

      const formData = new FormData();
      formData.append("name", record.Name);
      formData.append("email", record.Email);
      formData.append("subject", `Application for a Relevant Opportunity at ${record.Company}`);
      formData.append("message", `
        <p>Dear ${record.Name},</p>
        <p>I hope this message finds you well.</p>
        <p>I am reaching out to express my keen interest in any relevant opportunities at ${record.Company}. I am a Full-Stack Web Developer skilled in MERN stack.</p>
        <p>I have attached my resume and cover letter for your review.</p>
        <p>Looking forward to your response.</p>
        <p>Best regards,</p>
        <p><strong>Naveen M</strong><br/>
        <a href="https://www.linkedin.com/in/naveen4455">LinkedIn</a> | <a href="https://naveenm.in">Portfolio</a><br/>
        +91 9632557859</p>
      `);
      formData.append("attachments", coverLetterFile);

      try {
        console.log(`üì§ Sending email to ${record.Email}...`);
        const response = await sendemail(formData);

        if (response.status === "success") {
          console.log(`‚úÖ Success: Email sent to ${record.Email}`);
        } else {
          console.error(`‚ùå Failed: ${record.Email} - ${response.message}`);
          failedList.push({ ...record, error: response.message });
        }
      } catch (error) {
        console.error(`‚ùå Error sending email to ${record.Email} - ${error.message}`);
        failedList.push({ ...record, error: error.message });
      }

      setProgress(index + 1);

      if (index < data.Sheet1.length - 1) {
        console.log(`‚è≥ Waiting before sending the next email...`);
        await delay(3000 * Math.pow(2, failedList.length)); // Exponential Backoff
      }
    }

    setFailedEmails(failedList);
    setIsSending(false);

    if (failedList.length === 0) {
      alert("‚úÖ All emails sent successfully!");
    } else {
      alert(`‚ùå Failed to send ${failedList.length} email(s). Check the error log.`);
    }
  };

  return (
    <div>
      <h1>Send Bulk Emails</h1>
      <button onClick={handleSendEmails} disabled={isSending}>
        {isSending ? "Sending..." : "Send Emails"}
      </button>

      {totalEmails > 0 && (
        <div style={{ marginTop: "10px" }}>
          <progress value={progress} max={totalEmails} style={{ width: "100%" }} />
          <p>{progress}/{totalEmails} emails sent</p>
        </div>
      )}

      {failedEmails.length > 0 && (
        <div>
          <h2>Failed Emails:</h2>
          <ul>
            {failedEmails.map((record) => (
              <li key={record.Email}>
                {record.Name} ({record.Email}) - {record.Company} <br />
                <strong>Error:</strong> {record.error}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
