import { sendemail } from "./apicalls/email";
import { useState } from "react";
import { jsPDF } from "jspdf";

const data = {
  Sheet1: [
    {
      SNo: 1,
      Name: "kiran",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "kwhcbfqekfe",
    },
    {
      SNo: 2,
      Name: "arjun",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "Technologies",
    },
    {
      SNo: 3,
      Name: "puni",
      Email: "nm8602@srmist.edu.in",
      Title: "Associate Director HR",
      Company: "puni Technologies",
    },
  ],
};

// Function to add delay
const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

const App = () => {
  const [failedEmails, setFailedEmails] = useState([]);
  const [progress, setProgress] = useState(0);
  const [isSending, setIsSending] = useState(false);
  const [totalEmails, setTotalEmails] = useState(0);

  const generatePDF = async (record) => {
    const doc = new jsPDF();
    doc.setFont("times", "normal");
    doc.setFontSize(12);

    const currentDate = new Date().toLocaleDateString("en-US", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });

    const coverLetterText = `
  Naveen M
  naveenacp24@gmail.com | +91 9632557859
  LinkedIn: https://www.linkedin.com/in/naveen4455 | Portfolio: https://naveenm.in

  ${currentDate}

  ${record.Name}
  ${record.Company}
  ${record.Email}

  Subject: Application for a Relevant Role at ${record.Company}

  Dear ${record.Name},

  I am reaching out to express my keen interest in any relevant opportunities at ${record.Company}. I am a final-year Master of Computer Applications (MCA) student at SRM Institute of Science and Technology, with expertise in Full-Stack Web Development using the MERN stack (MongoDB, Express.js, React.js, and Node.js).

  I have gained valuable experience working as a Web Developer Intern, where I enhanced my technical skills and delivered successful projects. Additionally, I have independently completed freelance projects, including:

  ● Tuition Management System – Implemented features like student and admin management, attendance, and marks tracking, integrated with Twilio API for absent notifications.
  ● Company Portfolio Website – Built a professional website for client engagement with integrated contact features using Nodemailer.

  I am proficient in JavaScript, Python, REST API integration, and authentication mechanisms, enabling me to develop secure and efficient applications. I am highly motivated to apply my skills to contribute to ${record.Company}’s projects and goals.

  I have attached my resume for your review. I would welcome the opportunity to discuss how my skills align with your team’s needs. Please feel free to contact me at your convenience.

  Looking forward to your response.

  Best regards,
  Naveen M`;

    const lineHeight = 7;
    let yPosition = 10;

    coverLetterText.split("\n").forEach((line) => {
      doc.text(line, 10, yPosition);
      yPosition += lineHeight;
    });

    const pdfBlob = doc.output("blob");
    const sanitizedFileName = `Naveen_M_${record.Company.replace(/\s+/g, "_")}_Cover_Letter.pdf`;
    return new File([pdfBlob], sanitizedFileName, { type: "application/pdf" });
  };

  const handleSendEmails = async () => {
    setIsSending(true);
    setProgress(0);
    setFailedEmails([]);
    setTotalEmails(data.Sheet1.length);

    let failedList = [];

    for (const [index, record] of data.Sheet1.entries()) {
      const coverLetterFile = await generatePDF(record);
      console.log(coverLetterFile)
      const payload = {
        name: record.Name,
        email: record.Email,
        subject: `Application for a Relevant Opportunity at ${record.Company}`,
        message: `
          <p>Dear ${record.Name},</p>
          <p>I hope this message finds you well.</p>
          <p>I am reaching out to express my keen interest in any relevant opportunities at ${record.Company}. I am a final-year Master of Computer Applications (MCA) student at SRM Institute of Science and Technology, with expertise in Full-Stack Web Development using the MERN stack (MongoDB, Express.js, React.js, and Node.js).</p>
          <p>I have gained valuable experience working as a Web Developer Intern, where I enhanced my technical skills and delivered successful projects. Additionally, I have independently completed freelance projects, including:</p>
          <ul>
            <li>Tuition Management System – Implemented features like student and admin management, attendance, and marks tracking, integrated with Twilio API for absent notifications.</li>
            <li>Company Portfolio Website – Built a professional website for client engagement with integrated contact features using Nodemailer.</li>
          </ul>
          <p>My strong foundation in JavaScript, Python, API integration, and secure authentication positions me to handle complex development challenges.</p>
          <p>Please find my resume and cover letter attached for your reference. I would be grateful for an opportunity to discuss how my skills align with the needs of ${record.Company}.</p>
          <p>Thank you for considering my application. I look forward to the possibility of contributing to your team.</p>
          <p>Best regards,</p>
          <p><strong>Naveen M</strong><br/>
          <a href="https://www.linkedin.com/in/naveen4455">LinkedIn</a> | <a href="https://naveenm.in">Portfolio</a><br/>
          +91 9632557859</p>
        `,
        attachments: [coverLetterFile],
      };
      console.log(payload)

      try {
        console.log(`Sending email to ${record.Email}...`);
        const response = await sendemail(payload);

        if (response.status === "success") {
          console.log(`✅ Success: Email sent to ${record.Email}`);
        } else {
          console.error(`❌ Failed to send email to ${record.Email} - ${response.message}`);
          failedList.push(record);
        }
      } catch (error) {
        console.error(`❌ Error sending email to ${record.Email} - ${error.message}`);
        failedList.push(record);
      }

      setProgress(index + 1);

      if (index < data.Sheet1.length - 1) {
        console.log(`⏳ Waiting 3 seconds before sending the next email...`);
        await delay(3000);
      }
    }

    setFailedEmails(failedList);
    setIsSending(false);

    if (failedList.length === 0) {
      alert("✅ All emails sent successfully!");
    } else {
      alert(`❌ Failed to send ${failedList.length} email(s).`);
    }
  };

  return (
    <div>
      <h1>Send Bulk Emails</h1>
      <button onClick={handleSendEmails} disabled={isSending}>
        {isSending ? "Sending..." : "Send Emails"}
      </button>

      {totalEmails > 0 && (
        <div style={{ marginTop: "10px" }}>
          <progress value={progress} max={totalEmails} style={{ width: "100%" }} />
          <p>{progress}/{totalEmails} emails sent</p>
        </div>
      )}

      {failedEmails.length > 0 && (
        <div>
          <h2>Failed Emails:</h2>
          <ul>
            {failedEmails.map((record) => (
              <li key={record.Email}>{record.Name} ({record.Email}) - {record.Company}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
